import { ApplicationWhiteLabelDomain, ApplicationWhitelistDomain } from '@fleekxyz/gql-client-utils';

type WhitelistDomain = Omit<ApplicationWhitelistDomain, 'createdAt' | 'updatedAt' | '__typename'>;
type WhiteLabelDomain = Omit<ApplicationWhiteLabelDomain, 'createdAt' | 'updatedAt' | '__typename'>;

export const parseWhitelistDomains = ({
  whitelistDomains,
  whiteLabelDomains,
}: {
  whitelistDomains: WhitelistDomain[];
  whiteLabelDomains: WhiteLabelDomain[];
}) => {
  const combinedWhitelistDomains = [...whitelistDomains, ...whiteLabelDomains];
  const uniqueWhitelistDomains = Array.from(new Set(combinedWhitelistDomains));

  return uniqueWhitelistDomains;
};
